var opCount = 0;
var calculator = {
    add : function(x,y){
        ++opCount;
        return x + y;
        
    },
    subtract : function(x,y){
        ++opCount;
        return x - y;
    },
    multiply : function(x,y){
        ++opCount;
        return x * y;
    },
    divide : function(x,y){
        ++opCount;
        return x / y;
    },
    getOpCount : function(){
        return opCount;
    }
};
module.exports = calculator;